import React, { useEffect, useState } from 'react';
import fosseticImg from "../assets/images/fossetic.png";
import {useHistory} from 'react-router-dom'
import PropTypes from 'prop-types';
import axios from 'axios'



const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const API_URL = "http://localhost:8080/api/auth/";

  const handleSubmit = async () => {


    const credentials = {
        email: email,
        password: password
    };

    let res = await axios ({
      method: 'POST',
      url: 'http://localhost:8080/api/auth/signin',
      dataType: "JSON",
      contentType: false,
      processData: false,
      data: credentials
    });

    if(res.data) {
      console.log(res.data);
    } else {
      console.log('errreeeeeeeeuuuurrrrrr')
    }
  }

    /*await fetch('http://localhost:8080/api/auth/signin', {
      method: 'POST',
      headers: {
        "Content-Type":"application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(credentials)
    }).then((response) => {
      console.log(response);
    }).catch((error) => {
      console.log(error);
    });
  }*/



    /*axios.defaults.headers.post['Access-Control-Allow-Origin'] = '*';

    axios
      .post('http://localhost:8080/api/auth/signin', credentials)
      .then((response) => {
        if (response.data.accessToken) {
          localStorage.setItem("user", JSON.stringify(response.data));
        }

        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }*/

  return (

    <div className="auth-wrapper">
    <div className="auth-content">
        <div className="auth-bg">
            <span className="r"></span>
            <span className="r s"></span>
            <span className="r s"></span>
            <span className="r"></span>
        </div>
        <div className="card">
            <div className="card-body text-center">
                <div className="mb-4">
                <img src={fosseticImg} alt="" className="profile-img-card"/>

                </div>
                <h3 className="mb-4">Login</h3>
                <form onSubmit={handleSubmit}> 

                <div className="input-group mb-3">
                    <input type="email" className="form-control" name="email" placeholder="Email"  onChange={e => setEmail(e.target.value)}/>
                </div>
                <div className="input-group mb-4">
                    <input type="password" className="form-control" name="password"   placeholder="Mot de passe" onChange={e => setPassword(e.target.value)} />
              </div>
                <div className="form-group text-left">
                    <div className="checkbox checkbox-fill d-inline">
                        <input type="checkbox" name="checkbox-fill-1" id="checkbox-fill-a1" checked=""/>
                        <label for="checkbox-fill-a1" className="cr"> Se souvenir de moi</label>
                    </div>
                </div>

                 <input type="button" onClick={handleSubmit} value="Login" />
                <p className="mb-2 text-muted">Mot de passe oublié? <a href="auth-reset-password.html">Rejetter</a></p>


                </form>

            </div>
        </div>
    </div>
</div>




);
}







export default Login;
